import { useState, useCallback } from "react";
import { Link } from "react-router-dom";
import { isEmpty } from "../func";
import axios from "axios";
import "../App.css";


function SignupForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [auth, setAuth] = useState("ROLE_USER");
    const [nickname, setNickName] = useState("");
    const [realName, setRealName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");

    const inputEmail = useCallback(e => {
        setEmail(e.target.value);
    }, [])

    const inputPassword = useCallback(e => {
        setPassword(e.target.value);
    }, [])

    const inputNickName = useCallback(e => {
        setNickName(e.target.value);
    }, [])

    const inputRealName = useCallback(e => {
        setRealName(e.target.value);
    }, [])

    const inputPhoneNumber = useCallback(e => {
        setPhoneNumber(e.target.value);
    }, [])
    
    const setAdmin = useCallback(e => {
        setAuth("ROLE_ADMIN");
    }, [])

    const setUser = useCallback(e => {
        setAuth("ROLE_USER");
    }, [])


    const signUp = (e) => {
        e.preventDefault();
        if(isEmpty(email)){
            alert("You must input your Email");
            return Error;
        }else{setEmail(email);}

        if(isEmpty(password)){
            alert("You must input password");
            return Error;
        }else{setPassword(password);}

        axios.post('/user', {
            data : {
                email : email,
                password : password,
                auth : auth,
                nick_name : nickname,
                name : realName,
                phone : phoneNumber
            }
        }).then((res) => {
            alert("Register Successed.\nPlease login.");
            window.location.href="/login";
        }).catch((e) => {
            alert("Error. Please try again.\n" + e.response);
            window.location.replace("/login/signup");
        });
    }

    return (
        <div id="div-box">
            <br/>
            <h2>Sign Up</h2> <hr/>

            <form onSubmit={signUp}>
                <input type="text" name="name" placeholder="name" onChange={inputRealName}/> <br/>
                <input type="text" name="email" placeholder="E-Mail ( ID )" onChange={inputEmail}/> <br/>
                <input type="password" name="password" placeholder="Password" onChange={inputPassword}/> <br/>
                <input type="text" name="nickname" placeholder="nickname" onChange={inputNickName}/> <br/>
                <input type="text" maxLength={'11'} name="phone" placeholder="phone number" onChange={inputPhoneNumber}/> <br/>
                <p> ROLE : 
                    <input type="radio" name="auth" value="ROLE_ADMIN" style={{"width": "30px"}} onChange={setAdmin}/> ADMIN ||
                    <input type="radio" name="auth" value="ROLE_USER" defaultChecked="checked" style={{"width": "30px"}} onChange={setUser}/> USER <br/>
                </p>
                <button type="submit" id="btn-post">Join</button>
            </form>

            <Link id="none" to="/login"><button id="btn-default">Go to Login â†’</button></Link>
        </div>
    )
}

export default SignupForm;
