import { useState, useEffect } from "react";
import axios from "axios";
import { FetchWithoutId, isAdmin, isLogin } from "./func";
import _ from "lodash";
import 'w3-css';

function Bar() {
    const [categoryData, setCategorydata] = useState();

    useEffect(() => {
        if(_.isEmpty(categoryData)){
            FetchWithoutId(categoryData, setCategorydata, "category");
        }
    },[categoryData]);
    
    function myFunction() {
        var x = document.getElementById("demo");
        if (x.className.indexOf("w3-show") == -1) {
            x.className += " w3-show";
        } else { 
            x.className = x.className.replace(" w3-show", "");
        }
    }

    return ( 
        <>
        <div className="w3-top">
            <ul  className="w3-navbar w3-large w3-left-align" style={{backgroundColor:"#cab6ff"}}>
                <li  className="w3-hide-medium w3-hide-large w3-black w3-opennav w3-right">
                    <button onClick={() => myFunction()}>&#9776;</button>
                </li>
                <li> <button className="w3-bar-item w3-button w3-hover-deep-purple" onClick={() => window.location.href="/"}>Home</button> </li>
                <li  className="w3-hide-small"> <button className="w3-bar-item w3-button w3-hover-deep-purple"
                            onClick={() => {window.location.href = '/board'}}>Board</button> </li>
                <li className="w3-hide-small w3-dropdown-hover">
                    <button className="w3-button w3-hover-deep-purple" style={{marginLeft: "0"}}>Category</button>
                    <div  className="w3-dropdown-content w3-white w3-card-4">
                        {categoryData?.map((category, index) => (
                            <button key={index} className="w3-bar-item w3-button w3-light-gray w3-hover-deep-purple w3-border"
                                    onClick={() => window.location.href=`/board/${category.name}/${category.id}`}> {category.name} </button>
                        ))}
                    </div>
                </li>
                {isAdmin() && <li  className="w3-hide-small">
                        <button className="w3-bar-item w3-button w3-hover-red" onClick={() => window.location.href="/category"}>Setting</button> </li>}

                {!isLogin && 
                    <li className="w3-hid-small"><button className="w3-bar-item w3-button w3-hover-deep-purple w3-right"
                            onClick={() => window.location.href="/login/signup"}>Register</button></li>}
                {!isLogin && 
                    <li className="w3-hid-small"><button className="w3-bar-item w3-button w3-hover-deep-purple w3-right"
                            onClick={() => window.location.href="/login"}>Login</button></li>}
                {isLogin && 
                    <li className="w3-hid-small"><button className="w3-bar-item w3-button w3-hover-deep-purple w3-right"
                            onClick={() => window.location.href="/mypage"}>MyPage</button></li>}
                {isLogin && <li className="w3-hid-small"><button className="w3-bar-item w3-button w3-hover-red w3-right"
                    id="logout" onClick={() => Logout()}>Logout</button></li>}
            </ul>

            <div id="demo"  className="w3-hide w3-hide-large w3-hide-medium">
                <ul  className="w3-navbar w3-left-align w3-large w3-black">
                    <li> Home </li>
                    <li  className="w3-hide-small"> board </li>
                    <li  className="w3-dropdown-hover">
                        <button className="w3-button w3-hover-deep-purple" style={{marginLeft: "0"}}>Category</button>
                        <div  className="w3-dropdown-content w3-white w3-card-4">
                            {categoryData?.map((category, index) => (
                                <button key={index} className="w3-bar-item w3-button w3-light-gray w3-hover-deep-purple w3-border"
                                        onClick={() => window.location.href=`/board/${category.name}/${category.id}`}> {category.name} </button>
                            ))}
                        </div>
                    </li>
                    {isAdmin() && <li  className="w3-hide-small">
                        <button className="w3-bar-item w3-button w3-hover-red" onClick={() => window.location.href="/category"}>Setting</button> </li>}
                </ul>
            </div>
        </div>

        </>
        // <>
        // <div className="bar-top">
        //     <div className="w3-bar w3-large" style={{backgroundColor:"#cab6ff"}}>
        //         <div className="bar-left">
        //             <button className="w3-bar-item w3-button w3-hover-deep-purple" onClick={() => window.location.href="/"}>Home</button>
        //             <button className="w3-bar-item w3-button w3-hover-deep-purple"
        //                     onClick={() => {window.location.href = '/board'}}>Board</button>
                        
        //             <div className="w3-dropdown-hover">
        //                 <button className="w3-button w3-hover-deep-purple" style={{marginLeft: "0"}}>Category</button>
        //                 <div className="w3-dropdown-content w3-bar-block barcontent">
        //                     {categoryData?.map((category, index) => (
        //                         <button key={index} className="w3-bar-item w3-button w3-light-gray w3-hover-deep-purple w3-border"
        //                                 onClick={() => window.location.href=`/board/${category.name}/${category.id}`}> {category.name} </button>
        //                     ))}
        //                 </div>
